import ProductCard from "./components/ProductCard"
import React, { useEffect, useState } from 'react'
import { getProducts } from "./api";
import './App.css';

function App() {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    getProducts().then(response => {
      setProducts(response.data);
    }).catch(error => {
      console.error("Error fetching products:", error);
    });
  }, []);

  const addToCart = (product) => {
    setCartItems(prevItems => {
      const existingItem = prevItems.find(item => item.id === product.id);
      if (existingItem) {
        return prevItems.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevItems, { ...product, quantity: 1 }];
      }
    });
  };

  return (
    <div className="appcontainer">
      <div className="products-section">
        {products.map((product) => (
          <ProductCard key={product.id} items={product} addToCart={addToCart} />
        ))}
      </div>
      <div className="cart-section">
        <h2>Shopping Cart</h2>
        {cartItems.length === 0 ? (
          <p>Your cart is empty</p>
        ) : (
          <ul>
            {cartItems.map(item => (
              <li key={item.id}>
                <div><strong>{item.name}</strong></div>
                <div>Price: ${item.price}</div>
                <div>{item.description}</div>
                <div>
                  Quantity: 
                  <button onClick={() => {
                    setCartItems(prevItems => prevItems.map(i => i.id === item.id ? {...i, quantity: Math.max(i.quantity - 1, 1)} : i));
                  }}>-</button>
                  {item.quantity}
                  <button onClick={() => {
                    setCartItems(prevItems => prevItems.map(i => i.id === item.id ? {...i, quantity: i.quantity + 1} : i));
                  }}>+</button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default App
